import React from 'react' ; 
import { connect } from 'react-redux';

import { clearItemFromCart, addItem, removeItem } from '../../redux/cart/cart.actions';

import {
	CheckoutItemConatainer,
	ImageContainerContainer,
	ImageContainer,
	NameContainer,
	PriceCaontainer,
	QuantityCaontainer,
	ArrowContainer,
	ValueContiner,
	RemoveButtonContainer
} from './checkout-item.styles';

/*import './checkout-item.styles.scss';*/

const CheckoutItem = ({ cartItem, clearItem, addItem, removeItem }) => {
	const {name, imageUrl, price, quantity} = cartItem;
	return(
		<CheckoutItemConatainer>
			<ImageContainerContainer> 
				<ImageContainer src={imageUrl} alt='item'/>
			</ImageContainerContainer>
			<NameContainer> {name} </NameContainer>
			<QuantityCaontainer>
				<ArrowContainer onClick={ () => removeItem(cartItem) }>&#10094;</ArrowContainer>
				<ValueContiner> {quantity} </ValueContiner> 
				<ArrowContainer onClick={ () => addItem(cartItem) }>&#10095;</ArrowContainer>
			</QuantityCaontainer>
			<PriceCaontainer> {price} </PriceCaontainer>
			<RemoveButtonContainer onClick={() => clearItem(cartItem)}> &#10005; </RemoveButtonContainer>
		</CheckoutItemConatainer>
	)};

const mapDispatchToProps = dispatch => ({
	clearItem: item => dispatch( clearItemFromCart(item) ), 
	addItem: item => dispatch ( addItem(item) ),
	removeItem: item => dispatch( removeItem(item) )
})

export default connect(null, mapDispatchToProps)(CheckoutItem) ;
